const test = require('brittle')

test('set interval issue', async t => {
  setInterval(function () {}, 1000)

  const big = t.test('big test')
  const little = t.test('little test')
  big.plan(2)
  little.plan(1)

  little.pass('little passed')
  big.pass('big passed')

  await little

  console.log('after little passed')
})
